databaseChangeLog:
&propertiesAll;
DKG-196 Load template_part_content table with plain text samples.
DECLARE temp_blob BLOB := EMPTY_BLOB; BEGIN dbms_lob.createtemporary(temp_blob, TRUE); dbms_lob.open(temp_blob, dbms_lob.lob_readwrite); dbms_lob.append(temp_blob, utl_raw.cast_to_raw('Welcome message!')); dbms_lob.close(temp_blob);
MERGE INTO ${schema_name}.template_part_content t USING (SELECT 'XID7' as x__id, 'TEMPLATEPARTXID7' as template_part_id, temp_blob as content FROM DUAL ) x ON (t.x__id = x.x__id) WHEN MATCHED THEN UPDATE SET t.template_part_id = x.template_part_id, t.content = x.content, t.compressed = 0, t.x__moddate = SYSDATE, t.x__moduser= 'SYSTEM', t.x__version = t.x__version + 1 WHEN NOT MATCHED THEN INSERT (x__id, template_part_id, content, compressed, validity_start) VALUES (x.x__id, x.template_part_id, x.content, 0, TO_DATE('2023.07.01.','YYYY.MM.DD.')); END;
/
